import React, {Component} from 'react';
import styled from 'styled-components';
import Un from '../images/un.jpg';
import Arrow from '../images/arrow.png';
import github from '../images/github.png';
import linkedin from '../images/linkedin.png';
import { Link } from "react-scroll";

const Container = styled.div`
width: 100%;
min-height:100vh;
background-image:url(${Un});
background-size:100% 100%;
position: relative;
`

const Test = styled.div`
display: flex;
justify-content: center;
width:100%;
`
const NavArrow = styled.img`
position: absolute;
bottom: 10px;
left: 0px;
width:50px
right: 0px;
transition: 100ms;
display: block;
margin: 0 auto;
&:hover{
  transform: translateY(20px);
}

`
const Heading1 = styled.h1`
color:white;
text-align:center;
font-size:60px;
margin-top:-2%
margin-bottom: 4%;
@media(max-height:480px){
  font-size:40px;
}
`
const Heading2 = styled.p`
color:white;
margin-top:2%
text-align:center;
text-transform:uppercase;
`
const IconContainer = styled.div`
display:flex;
justify-content:center;
width:100%;
`
const Image = styled.img`
width:70px
margin:1.5%;
@media(max-height:641px){
  width:45px
}
` 
const InnerContainer = styled.div`
display:flex;
justify-content:center;
flex-direction: column;
height: 100vh;
background:#00000085;
`   
const Btn = styled.button`
padding:10px 30px;
background:#d0d0d038;
color:white;
border:none;
margin-right:10px;
transition: ease-in 200ms;
border-bottom: 2px #5953ff solid;
&:hover{
  transform: scale(1.2);
  margin:0px 20px;
}
`   
const BtnGroup = styled.div`
display: flex;
justify-content: center;
width:100%;
margin-top:5%
` 
const D = styled.p`
display: flex;
justify-content: center;
width:100%;
position: absolute;
bottom:40px;
color:white;
`   

class Jumbotron extends Component {

  render() { 
    return ( 

        <Container id="Nav">
           <InnerContainer >
           <div><Heading1>{this.props.text}</Heading1></div>

            <IconContainer>  <Image src= {linkedin} alt=""/> <Image src= {github} alt=""/></IconContainer>
            <div><Heading2>{this.props.text2}</Heading2></div>
            <BtnGroup>
              <Btn>Resume</Btn>
              <Link
              activeClass="active"
              to="ContactMe"
              spy={true}
              smooth={true}
              offset={-50}
              duration= {500}><Btn>Contact Me</Btn></Link>
            </BtnGroup>
            <Test>
              <D>Projects</D>
            <Link
              activeClass="active"
              to="Skills"
              spy={true}
              smooth={true}
              offset={-50}
              duration= {500}><NavArrow  src = {Arrow} alt=""/></Link>
            </Test>
           </InnerContainer >
        </Container>

    );
  }
}

export default Jumbotron;
